class SmoothedRandom{
  int duration;
  final int eachSide = 3;
  SmoothedRandom(int duration){
    this.duration = duration;
    queue = new ArrayDeque(eachSide * 2);
    for(int c = 0; c < eachSide * 2; c++){
      queue.addLast(Math.random());
    }
    progress = 0;
  }
  Deque<Double> queue;
  int progress;
  double get(){
    progress++;
    if(progress >= duration){
      progress = 0;
      queue.removeFirst();
      queue.addLast(Math.random());
    }

    double loc = eachSide - 1 + (double)progress / duration;
    double num = 0;
    double den = 0;

    int c = 0;
    for(Double n : queue){

      double weight = - Math.pow((double)c - loc, 2) + Math.pow(eachSide, 2);
      den += weight;
      num += weight * n;
      c++;
    }

    return num / den;
  }
}

