        r * floor(valueFromDeck()) * 1.5,
        r * floor(valueFromDeck()) * 0.1
      );
      push();
      translate(0, height / 2);
      let r2 = r * floor(valueFromDeck()) * 0.2;
      ellipse(col + 2, row, r2, r2);
      pop();
    }
