      push();
      translate(r, r);
      ellipse(col, row, r * floor(valueFromDeck()) * 0.2, r * 2);
      pop();
    }
  }

  fill(a * 0.5 + 0.5, 1, 1 - a, 0.8);
  for (let col = step; col < width - step; col += step) {
    for (row = (height * 3) / 8; row < (height * 5) / 8; row += step) {
      ellipse(col, row, r * floor(valueFromDeck()) * 0.2, r);
    }
