function getQuick(img, x, y) {
  if (x < 0 || x >= img.width || y < 0 || y >= img.height) return
  var i = (y * img.width + x) * 4;
  return [
    img.pixels[i],
    img.pixels[i + 1],
    img.pixels[i + 2],
    img.pixels[i + 3],
  ];
}

function setQuick(img, x, y, c) {
  if (x < 0 || x >= img.width || y < 0 || y >= img.height) return
  var i = (y * img.width + x) * 4;
  for (let f = 0; f < 4; f++) {
    img.pixels[f + i] = c[f];
  }
}

function compareQuick(a, b) {
  for (let i = 0; i < 3; i++) {
    if (a[i] != b[i]) {
      return false;
    }
  }
  return true;
}

