function polygon(x, y, radius, npoints) {
  let angle = TWO_PI / npoints;
  beginShape();
  for (let a = 0; a < TWO_PI; a += angle) {
    let sx = x + cos(a-HALF_PI) * radius;
    let sy = y + sin(a-HALF_PI) * radius;
    vertex(sx, sy);
  }
  endShape(CLOSE);
}

