// A basic Truchet tiler for Computational Form
// 
//	NUM defines scale
//  UST_TIME lets you create random tiles and pause on one, use 1 or 0 as value
//


#define NUM 5.			//scale size
#define COL_OFF 4.3		//hue offset
#define USE_TIME 1		//create new tiles or not


//http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl
// All components are in the range [0â€¦1], including hue.
vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

// All components are in the range [0â€¦1], including hue.
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

// Gold Noise Â©2015 dcerisano@standard3d.com
// - based on the Golden Ratio
// - uniform normalized distribution
// - fastest static noise generator function (also runs at low precision)

float PHI = 1.61803398874989484820459;  // Î¦ = Golden Ratio   

float gold_noise(in vec2 xy, in float seed){
       return fract(tan(distance(xy*PHI, xy)*seed)*xy.x);
}

float sdCircle( vec2 p , vec2 offSet, float r){
	return length ( p - offSet) - r;
}

//give it a uv position
float truchetTile(vec2 p, vec2 cell)
{
    float r = 1./100000.; ///why is this... the value I have to use?

    float pipe = 0.;
    float pipe2 = 0.;
    #if USE_TIME
        if (gold_noise(cell+ floor(iTime*.7), .3) > .5){
    #else
    	if (gold_noise(cell+ 1.,3.) > .5){
    #endif
        pipe =  min(sdCircle( p, vec2(0,0), 0.),  1.-sdCircle( p, vec2(0,0), 0.));
		pipe2 = min(sdCircle( p, vec2(1,1), 0.),1.-sdCircle( p, vec2(1,1), 0.));
    }else{
        pipe =  min(sdCircle( p, vec2(1,0), 0.),  1.-sdCircle( p, vec2(1,0), 0.));
        pipe2 = min(sdCircle( p, vec2(0,1), 0.),1.-sdCircle( p, vec2(0,1), 0.));
    }

    float sizeMod = .4 + .045*sin(.3*iTime);
    float thickness = .05;
    float blur = 10.0;
	float value = max(smoothstep(sizeMod,sizeMod+thickness, pow(pipe,1.)),smoothstep( sizeMod ,sizeMod+thickness, pipe2));
    return pow(max(value,0.),blur);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv = fragCoord/iResolution.xy;

    vec2 uvLoop = fract((uv) * NUM);
    vec2 uvCells = floor(uv * NUM);

    vec3 color = vec3(.784, .635, .784);
    vec3 bg = vec3(.294,0.,.51);


    float off = sin(iTime*.2);
    float colOffset = off;
    float bgOffset = off;
    vec3 hsvC = rgb2hsv(color);
    vec3 hsvBG = rgb2hsv(bg);
    hsvC = fract( hsvC + colOffset);
    hsvBG = fract( hsvBG + bgOffset);
    color = hsv2rgb(hsvC);
    bg = hsv2rgb(hsvBG);
    float inPipe = truchetTile(uvLoop, uvCells);
    color = color * inPipe + (1.-inPipe) * bg;

    fragColor.xyz = color;
}

