      const noisefreq = 15;
      var x = noise(i* noisefreq, 0) * width
      var y = noise(i* noisefreq, 10000) * height

      //Seeing similar sizes near each other, I set the diameter by the noise of each dot's coordinates
      const diafreq = .3
        var diameter = noise(x * diafreq, y * diafreq) * 25 + 1;

      //Seeing a progression from red/yellow on smaller dots, green/blue medium dots, and magenta large dots, I mapped hue to dot size.
      colorMode(HSB, 100)
      fill(map(diameter, 4, 23, 0, 100), 100, 100)

        ellipse(x, y, diameter, diameter);
    }


    noLoop();


}

