function drawBranch(length) {
  if (length < 6) {
    return;
  }

  // draw this branch
  let l1 = (length * random(0.6, 1)) / 8;
  strokeWeight(l1);
  myTurtle.turnRight(random(-10, 10));
  myTurtle.moveForward(length);
  myTurtle.turnRight(random(-10, 10));
  myTurtle.moveForward(l1 * 8);

  // left child
  myTurtle.pushState();
  myTurtle.turnLeft(20 * random(-0.5, 1));
  let l2 = l1 * random(0.8, 1);
  strokeWeight(l2);
  if (c2 == true) {
    stroke(285 - l2 * 80, 225 - l2 * 80, 20);
  } else {
    stroke(285 - l2 * 80, 20, 20);
  }
  drawBranch(l2 * 8);

  myTurtle.popState();

  // right child
  myTurtle.pushState();
  myTurtle.turnRight(20 * random(-0.5, 1));
  let l3 = l1 * random(0.8, 1);
  strokeWeight(l3);
  if (c2 == true) {
    stroke(285 - l2 * 80, 225 - l3 * 80, 20);
  } else {
    stroke(285 - l2 * 80, 20, 20);
  }
  drawBranch(l3 * 8);

  myTurtle.popState();

  // left2
  myTurtle.pushState();
  myTurtle.turnLeft(40 * random(-0.5, 1));
  let l4 = l1 * random(0.8, 1);
  //if (random() > 0.2) {
  strokeWeight(l4);
  if (c2 == true) {
    stroke(285 - l4 * 80, 225 - l4 * 80, 20);
  } else {
    stroke(285 - l4 * 80, 20, 20);
  }
  drawBranch(l4 * 8);
  //}
  myTurtle.popState();

  // right2
  myTurtle.pushState();
  myTurtle.turnRight(40 * random(-0.5, 1));
  let l5 = l1 * random(0.8, 1);
  //if (random() > 0.2) {
