let snowflakes = []; // array to hold snowflake objects
let speedX, speedY, size, spiral, moon, c;

function setup() {
  createCanvas(640, 640);
  createSpan("x speed");
  speedX = createSlider(1, 20, 10);
  createSpan("y speed");
  speedY = createSlider(1, 20, 10);
  createSpan("particle size");
  size = createSlider(2, 5, 3);
  createSpan("particle color");
  c = createSlider(0, 10, 7);
  createP();

  createSpan("moon size");
  spiral = createSlider(150, width / 2, 300);
  createSpan("moon height");
  moon = createSlider(0, height, 0);

  colorMode(HSB, 1);
}

function draw() {
  background(0, 0.1);

  let t = frameCount / 60; // update time

  // create a random number of snowflakes each frame
  for (let i = 0; i < random(3); i++) {
    snowflakes.push(new snowflake()); // append snowflake object
  }

  // loop through snowflakes with a for..of loop
  for (let flake of snowflakes) {
    flake.update(t); // update snowflake position
    flake.display(); // draw snowflake
  }

  fill(0);
  noStroke();
  ellipse(width / 2, moon.value(), spiral.value());
}

// snowflake class
function snowflake() {
  // initialize coordinates
  this.posX = 0;
  this.posY = random(-50, 0);
  this.initialangle = random(0, 2 * PI);
  this.size = random(0.5, size.value());

  // radius of snowflake spiral
  // chosen so the snowflakes are uniformly spread out in area
  this.radius = sqrt(random(pow(spiral.value(), 2)));

  this.update = function (time) {
    // x position follows a circle
    let angle = speedX.value() * 0.01 * time + this.initialangle;
    this.posX = width / 2 + this.radius * sin(angle);

    // different size snowflakes fall at slightly different y speeds
    this.posY += pow(this.size, 0.5) * speedY.value() * 0.1;

    // delete snowflake if past end of screen
    if (this.posY > height) {
      let index = snowflakes.indexOf(this);
      snowflakes.splice(index, 1);
    }
  };

  this.display = function () {
    if (random() > 0.5) {
      stroke(c.value() * 0.1, 1, 1, 1 - (this.posY / height) * 1);
    } else {
      stroke(1 - (this.posY / height) * 1);
    }
    strokeWeight(this.size);
    point(this.posX, this.posY);
    noStroke();
  };
}

