function setup() {
  createCanvas(400, 400);
  angleMode(DEGREES)
  t=0
}

function draw() {
  background(0)
  noStroke() 
  let l=sin(t)+9
  for (let f=30;f>0;f--){
    beginShape();
    for (let i=0;i<360;i+=360/(f+2)){
      let x=width/2+cos(i+t/f)*f*l;
      let y=height/2+sin(i+t/f)*f*l;
      vertex(x,y);
      fill(255-f*11)
      ellipse(x,y,l);
      fill(255-f*12)
    }
    endShape(CLOSE);
  }
  t+=2
}

