var amplitude, frequency, step, startX, endX, startY, endY;

function setup() {
  createCanvas(500, 500);
  colorMode(HSB, 1);
  createSpan("frequency");
  frequency = createSlider(0, 50, 25);
  createSpan("amplitude");
  amplitude = createSlider(0, 2, 1, 0.1);
  createSpan("step");
  step = createSlider(0.002, 0.02, 0.01, 0.0001);
  createP();
  createSpan("startX");
  startX = createSlider(0, 1, 0, 0.1);
  createSpan("endX");
  endX = createSlider(0, 1, 1, 0.1);
  createSpan("startY");
  startY = createSlider(0, 1, 0, 0.1);
  createSpan("endY");
  endY = createSlider(0, 1, 1, 0.1);
}

function draw() {
  background(255);
  ellipseMode(CENTER);
  noiseDetail(1, 0.5);

  let offsetX, offsetY;

  for (i = 0; i < 1; i += step.value()) {
    var x = lerp(startX.value(), endX.value(), i);
    var y = lerp(startY.value(), endY.value(), i);

    offsetX =
      map(noise(i * frequency.value() + millis() * 0.002), 0, 1, -1, 1) *
      amplitude.value();

    offsetY =
      map(noise(i * frequency.value() + millis() * 0.002), 0, 1, -1, 1) *
      amplitude.value();

    strokeWeight(step.value() * 250);
    stroke(x + offsetX, 1, 1);
    line(0, i * height, width, i * height);
    stroke(y + offsetY, 1, 1);
    line(i * width, 0, i * width, height);
  }
}
