new Path.Rectangle([0, 0],[1000, 1000]).fillColor = "#fce597"
var mouthclosed = new Raster("https://cdn.glitch.com/85358b14-f8e2-4925-8d91-62c517f498f8%2Fg930.png?v=1604716814160")
mouthclosed.position = view.center + [0, -108];
mouthclosed.scale(.5)
var mouthopen = new Raster("https://cdn.glitch.com/85358b14-f8e2-4925-8d91-62c517f498f8%2Fg924.png?v=1604716811618");
mouthopen.position = view.center + [0, -180];
mouthopen.scale(.5)
mouthopen.opacity = 0

var chompregion = new Path.Ellipse([170, 80], [160, 200])

var greenslice = new Path([
    new Segment([250, 30]),
    new Segment([150, 180], [0, 0], [40, 30]),
    new Segment([350, 180], [-40, 30]),
    new Segment([250, 30]),
])
greenslice.fillColor = "green"

var redslice = new Path([
    new Segment([250, 30]),
    new Segment([163, 160], [0, 0], [40, 25]),
    new Segment([337, 160], [-40, 25]),
    new Segment([250, 30]),
])
redslice.fillColor = "red"

var food = new Group([
    greenslice,
    redslice
]);

for(var cc = 0; cc < 16; cc++){
    var pt;
    do{
        pt = [Math.random() * (337 - 164) + 150, Math.random() * (160 - 30) + 30 ]
    }while(!redslice.contains(pt))
    var ellipse = new Path.Ellipse(pt, [6, 10])
    ellipse.fillColor = "black"
    food.addChild(ellipse)
}

food.scale(1.5)

function processChomp(){
    var items = food.removeChildren();
    for(var c = 0; c < items.length; c++){
        food.addChild(items[c].subtract(chompregion))
        items[c].remove();
    }
}

function onKeyDown(event){
    mouthopen.opacity = 1;
    mouthclosed.opacity = 0;
}
function onKeyUp(event){
    mouthopen.opacity = 0;
    mouthclosed.opacity = 1;
    processChomp();
}
function onMouseDrag(event){
    food.position = event.point
}
