// observations
// frequency adjusts how tightly the individual ellipses are (x?)
// higher the frequency -> less uniform the wave movement
// amplitude adjusts the displacement of the ellipses (y?)
// higher the amplitude -> higher the displacement (dips in waves)
// time speed adjusts how quickly the wave is moving, dependent on amplitude
// faster the time -> faster the movement


var frequency_slider;
var amplitude_slider;
var time_slider;

var startX = 50;
var startY = 250;
var endX = 450;
var endY = 50;

var t = 0;


function setup() {
    createCanvas(500, 300);


    createP('Frequency');
    frequency_slider = createSlider(0, 100, 50);

    createP('Amplitude');
    amplitude_slider = createSlider(0, 100, 50);

    createP('Time Speed');
    time_slider = createSlider(0, 5, 2);


}


function draw() {
    background(50);
    ellipseMode(CENTER);

    var frequency = frequency_slider.value() / 10;
    var amplitude = amplitude_slider.value() / 10;
    var time = time_slider.value() / 1000;


    noiseDetail(1, .5);



    fill(255);
    noStroke();

    for (i = 0; i < 1; i += .02) {
        var x = lerp(startX, endX, i);
        var y = lerp(startY, endY, i);

        // vary over x + time and y + time?
        var offsetX = (noise(i * frequency + t)) * amplitude * 10;
        var offsetY = (noise(i * frequency + t)) * amplitude * 10;

       t += time;

        ellipse(x + offsetX, y + offsetY, 10, 10);

    }



}

