function valueFromDeck() {
	let v = values[position];

	position++;

	if (position == values.length) {
	  values = shuffle(values);
	  position = 0;
	}

	return v;
  }

  function colorFromDeck() {
	let c = colors[position];

	position++;

	if (position == colors.length) {
		colors = shuffle(colors);
		position = 0;
	  }

	return c;
  }

