    var myTurtle;

    function setup() {
      createCanvas(windowWidth, windowHeight);
      frameRate(240);
      noFill();
      stroke(255);
      background(0);
      myTurtle = new Turtle();
    }

    function draw() {
      myTurtle.penUp();
      myTurtle.moveTo(windowWidth / 2, windowHeight);
      myTurtle.turnTo(-80);
      myTurtle.penDown();
      drawBranch(100);

    }


    function drawBranch(length) {

      if (length  < 4) {
        return;
      }

      // draw this branch
      strokeWeight(length / 20);
      myTurtle.moveForward(length * .2);
      myTurtle.turnLeft(4);
      myTurtle.moveForward(length * .2);
      myTurtle.turnLeft(4);
      myTurtle.moveForward(length * .8);

      // left child
      myTurtle.pushState();
      myTurtle.turnLeft(random(4, 22));
      drawBranch(length * 0.75);
      drawBranch(length * random(.4, .8));
      myTurtle.popState();

      // right child
      myTurtle.pushState();
      myTurtle.turnRight(random(4, 42));
      drawBranch(length * 0.75);
      drawBranch(length * random(.4, .8));
      myTurtle.popState();

    }

