var aSs, fSs, tSs;
let t = 0;
var X0 = 50;
var Y0 = 250;
var Xx = 450;
var Yy = 50;
function setup() {
  createCanvas(500, 300);
  createP("Frequency");
  fSs = createSlider(0, 100, 50);
  createP("Amplitude");
  aSs = createSlider(0, 100, 40);
  createP("TimeSpeed");
  tSs = createSlider(0, 100, 50);
}
function draw() {
  background(50);
  ellipseMode(CENTER);
  var a = aSs.value();
  var f = fSs.value() / 2;
  var tS = tSs.value() / 1000;
  noiseDetail(1, 0.5);
  fill(255);
  noStroke();
  t += tS;
  // study this loop. do you understand how the line of ellipses is created?
  for (i = 0; i < 1; i += 0.02) {
    var x = lerp(X0, Xx, i);
    var y = lerp(Y0, Yy, i);
    var oX = noise(i * f + t) * a;
    var oY = oX;
    ellipse(x + oX, y + oY, 10, 10);
  }
}

