let font;
let points;
let bounds;
var EXPORT = false;
var fade;
var fadeAmount = 1;
var fadefill = 0;
let x1, y1;

function preload() {
  font = loadFont("assets/Lobster/Lobster-Regular.ttf");
}

function setup() {
  pixelDensity(1);
  createCanvas(1000, 480);
  frameRate(60);
  ellipseMode(CENTER);
  noiseDetail(1);
  fade = 0;
  textSize(50);
  createCanvas(windowWidth, windowHeight);
  points = font.textToPoints("Comp Form 2021", 0, 0, 200, {
    sampleFactor: 1,
    simplifyThreshold: 0,
  });
  bounds = font.textBounds("Comp Form 2021", 0, 0, 200);
}

function draw() {
  background(0);
  blendMode(BLEND);
  background(0);

  blendMode(ADD);
  fill(1);
  var x = width * 0.5;
  var y = height * 0.5;

  for (i = 0; i < 5000; i++) {
    var s = sin((frameCount / 90) * PI) * 100;

    var offsetX = map(
      noise(i * 0.5, frameCount * 0.01, 0),
      0,
      0.5,
      -1000,
      1000
    );
    var offsetY = map(noise(i * 0.5, frameCount * 0.01, 1), 0, 0.5, -480, 480);

    ellipse(x + offsetX, y + offsetY, s, s);
  }

  textFont(font, 108);
  textAlign(CENTER);

  fade = map(frameCount, 0, 90, 0, 255);
  if (fade < 0) fadeAmount = 1;

  if (fade > 255) fadeAmount = -10;

  fade += fadeAmount;

  if (frameCount > 90) {
    frameCount = 0;
  }
  //console.log(fade);
  //console.log(frameCount);

  //console.log(fadeAmount);
  fill(255, 0, 0, fade / 4);

  noStroke();

  if (frameCount < 50) {
    letterAppear1 = map(frameCount, 0, 50, 0, width / 2);
    letterAppear2 = map(frameCount, 0, 50, 0, height / 2);
    x1 = width - letterAppear1;
    y1 = height - letterAppear2;
  }
  if (frameCount > 50 && frameCount < 70) {
    x1 = width / 2;
    y1 = height / 2;
  }
  letterAppear3 = map(frameCount, 120, 70, 0, width / 2);
  letterAppear4 = map(frameCount, 120, 70, 0, height / 2);
  if (frameCount > 70) {
    x1 = letterAppear3;
    y1 = letterAppear4;
  }

  let centerDist = dist(x1, y1, width / 2, height / 2);

  let jiggle = map(centerDist, 0, width, 2, 1000);
  //console.log(jiggle);
  let cx = map(centerDist, 0, width, 5, 10);
  let cy = map(centerDist, 0, width, 5, 10);
  translate((width - abs(bounds.w)) / 2, (height + abs(bounds.h)) / 2);
  //console.log(jiggle);

  for (let i = 0; i < points.length; i++) {
    let p = points[i];
    ellipse(
      p.x + jiggle * randomGaussian(),
      p.y + jiggle * randomGaussian(),
      cx,
      cy
    );
  }

  //noLoop();
}

