function makeSlider() {

    noFill();
    strokeWeight(4);


  for (let x = 0; x < width; x += 10) {

    stroke(255);
    point(x, random(height));

    for (let y = 0; y < height; y += 10) {

      let val = slider.value();
      let v = sin(x * val * 0.01)

      stroke(255 * v + x, 0, 255);
      strokeWeight(4);

      curve(
        x * PI * v,
        y * PI * v,
        x * PI * v,
        y * PI * v,
        x * PI * v,
        y * PI * v,
        x * PI,
        y * PI
      );

      strokeWeight(0.5);
      curve(
        10 + x * PI * v,
        10 + y * PI * v,
        x * 10 * PI * v,
        y * 10 * PI * v,
        x * 10 * PI * v,
        y * 10 * PI * v,
        x * 10 * PI,
        y * 10 * PI
      );


    }



  }

}

