function setup() {
  createCanvas(800, 800);
  xPos = 400;
  yPos = 675;
}// end setup

function draw() {
  frameRate(20);
  background(20);
  createLauncher();
  createText();
  createUpwardBlock();

  r = random(255);
  g = random(255);
  b = random(255);

  if (yPos < 260) {
    xPos = -50;
    yPos = -50;

    m = m + 1;

    stroke(r, g, b);
    createExplosion(m);

    //line(x1,y1,x2,y2)
    push();
    if (m > 55) {
      frameRate(30);
      translate(height / 2, width / 8);
      translate(400, -70);
      rotate(90);
      createExplosion(m);
    }
    if (m > 85) {
      frameRate(40);
      translate(height / 2, width / 8);
      translate(420, -70);
      rotate(90);
      createExplosion(m);
    }
    pop();
    if (m > 125) {
      createSmiley();
    }
    if (m > 199) {
      m = 0;
      xPos = 400;
      yPos = 675;
    }
  }
  xPos = 395;
  yPos = yPos - 5;
}// end draw

function createLauncher() {
  push();
  fill(50);
  stroke(0);
  strokeWeight(5);
  rect(350, 700, 100, 100);
  pop();
}

function createText() {
  push();
  noStroke();
  fill(0, 102, 153);
  textSize(32);
  text("2021", 370, 758);
  textAlign(CENTER, CENTER);
  pop();
}
function createExplosion(m) {
  line(250 - m, 250, 350 - m, 250);
  line(450 + m, 250, 550 + m, 250);

  line(400, 250 - m, 400, 150 - m);
  line(400, 250 + m, 400, 350 + m);

  line(325 - m, 325 + m, 400 - m, 250 + m);
  line(400 + m, 250 + m, 475 + m, 325 + m);

  line(400 - m, 250 - m, 325 - m, 175 - m);
  line(400 + m, 250 - m, 475 + m, 175 - m);
  // line(450+m,250,550+m,250)
}
function createUpwardBlock() {
  stroke(200);
  fill(200, 10, 0);
  rect(xPos, yPos, 10, 20);
}

function createSmiley() {
  push();
  fill("yellow");
  stroke("yellow");
  strokeWeight(4);
  rect(350, 300, 10, 30);
  rect(440, 300, 10, 30);
  noFill();
  arc(400, 380, 100, 100, 0.1 * PI, 0.9 * PI);
  pop();
}

