function setup() {
  createCanvas(400, 400);
}

function draw() {
  background(255);

  noStroke();
  noiseDetail(1);

  var w = 2;

  var xoff = 0;
  for (let x = -width; x <= width; x += w){
    yoff = 0;
    for (let y = -height; y <= height; y += w) {
      var h = map (noise(xoff, yoff), 0, 1, -100, 100);

      var r = map(x, -width + h, width + h, 0, 255);
      var g = map (y, -height + h, height + h, 0, 255)
      var b = map(h, -100,100, 0, 255)
      push()
      fill (r/1.3 , g, b)
      translate (x, y);
      ellipse (w, w, h, h)
      pop()

      yoff += 0.1
    }
    xoff += 0.1
  }
  noLoop()
}

