function raining(x, y) {

  this.xpos = x;
  this.ypos = y;
  this.radius = 1;
  this.rainlength = 12;
  this.rainopacity = 100;

  //raining now..
  this.raindropping = function() {
    noStroke();
    // fill(random(96,107), random(164,110),random(255,99)); 
    fill('#ABE9FA')
    ellipse(this.xpos, this.ypos, this.radius, this.rainlength)
    this.ypos = this.ypos + 64;
    if (this.ypos > 480) {
      this.rainlength = this.rainlength - 5 

    } 
    if (this.rainlength < 0) {
      this.rainlength = 0;
    }
  }

}

