		fill(colorPicker);
		let diameterPicker2 = getDiameter2();
		let xnew = getXCoord();
		ellipse(xnew+210, y*4, diameterPicker2);
	}
	for (let y = 0; y <= height; y ++) {
		let colorPicker = getColor();
		fill(colorPicker);
		let diameterPicker3 = getDiameter3();
		let xnew = getXCoord();
		ellipse(xnew+270, y*4, diameterPicker3);
	}
	for (let y = 0; y <= height; y ++) {
		let colorPicker = getColor();
		fill(colorPicker);
		let diameterPicker = getDiameter();
		let xnew = getXCoord();
		ellipse(xnew+360, y*4, diameterPicker);		
	}
	for (let y = 0; y <= height; y ++) {
		let colorPicker = getColor();
		fill(colorPicker);
		let diameterPicker2 = getDiameter2();
		let xnew = getXCoord();
		ellipse(xnew+390, y*4, diameterPicker2);
	}
	for (let y = 0; y <= height; y ++) {
		let colorPicker = getColor();
		fill(colorPicker);
		let diameterPicker3 = getDiameter3();
		let xnew = getXCoord();
		ellipse(xnew+440, y*4, diameterPicker3);
	}
}

function getColor() {
	let c = colors[position];

	position++;

	if (position == colors.length) {
		colors = shuffle(colors);
		position = 0;
	}
	return c;
}


function getDiameter() {
	let d = diameter[position];

	position++;

	if (position == diameter.length) {
		diameter = shuffle(diameter);
		position = 0;
	}
	return d;
}

function getDiameter2() {
	let d2 = diameter2[position];

	position++;

	if (position == diameter2.length) {
		diameter2 = shuffle(diameter2);
		position = 0;
	}
	return d2;
}


function getDiameter3() {
	let d3 = diameter3[position];

	position++;

	if (position == diameter3.length) {
		diameter3 = shuffle(diameter3);
		position = 0;
	}
	return d3;
}

function getXCoord() {
	let xUpdated = xCoord[position];

	position++;

	if (position == xCoord.length) {
		xCoord = shuffle(xCoord);
		position = 0;
	}
	return xUpdated;
}

