  osc2.amp(.5);

  osc3 = new p5.Oscillator();
  osc3.setType('sine');
  osc3.amp(.5);

values = shuffle(values);

for (let i =0; i < values.length; i++) {
  console.log(valueFromDeckA());
  console.log(valueFromDeckB()); 
}

}

function valueFromDeckA() {
  // find the value at the current position in the deck
  var v = values[positionA];

  osc.freq(v);
  osc3.freq(v-100);

  // change the position for next time
  positionA++;

  // if we run out of "cards", shuffle and start over from the top
  if (positionA == values.length) {
    values = shuffle(values);
    positionA = 0;
  }

  // return the value
  return v;
}

function valueFromDeckB() {
  // find the value at the current position in the deck
  var v = values[positionB];

  osc2.freq(v);

  // change the position for next time
  positionB--;

  // if we run out of "cards", shuffle and start over from the top
  if (positionB == 0) {
    values = shuffle(values);
    positionB = 6;
  }

  // return the value
  return v;
}






function draw() {

  let currentWave = valueFromDeckA() + valueFromDeckB();
  background(random(currentWave/8 + 50,values.length),random(currentWave/8 + 50,values.length),random(currentWave/8 + 50,values.length));
  console.log(currentWave/24);



  osc.start();
  osc2.start();
  osc3.start();


}

