function generateTitle() {
	var abstracts = ["Love", "Lover", "Friendship", "Affection", "Care", "Understanding", "Language", "Community"];
	var actions = ["Hurts", "Stays", "Touches", "Hits", "Brightens", "Dims", "Shines", "Creates", "Says", "Cries", "Dances", "Speaks", "Surprises", "Disappoints", "Betrays", "Lies", "Tempts"];
	var descriptions = ["Lost", "Happy", "Wet", "Forgotten", "Fallen", "Delightful", "Valiant", "Golden", "Painful", "Merry", "Brilliant", "Wonderful", "Sullen", "Angry", "Fantastical", "Cowardly", "Silver", "Tragic", "Jolly", "Festive", "Mad", "Enchanted", "Cruel", "Hopeful", "Dark", "Untold"];
	var objects = ["Garden", "Hell", "Heaven", "Dream", "Word", "Dialect", "Promise", "Lie", "Darkness", "Letter", "Confession", "Mouth", "Shadow", "Tear", "Homeland", "Forest", "Bedroom", "Book", "Hand", "Lip", "Night", "Day", "Body"];

	var abstract = sample(abstracts);
	var action = sample(actions);
	var description = sample(descriptions);
	var object = sample(objects);

	var title = "";
	if (Math.random() < 0.5) {
		title = `${abstract} ${action} in ${description} ${object}`;
	}
	else {
		title = `${abstract} is ${description} when ${object} ${action}`;
	}

	return title;
}

function generateSubtitle() {
var adjectives = ["Lost", "Forgotten", "Tragic", "Sad", "Wonderful", "Limited", "Crazy", "Iconic", "Dramatic", "Incredible", "Unbelievable", "Enchanted", "Wise", "Remastered", "Dark", "Untold", "Psychotic"];	var formats = ["Tale", "Novel", "Book in Three Parts", "Song", "Short Story", "Legend", "Mystery", "Comedy", "Musical", "Indie Film", "Lecture Series"];

	var adjective = sample(adjectives);
	var format = sample(formats);
	var subtitle = `The  ${adjective}  ${format}`;

	return subtitle;
}

function sample(array) {
	const index = Math.floor(Math.random() * array.length);
	return array[index];
}

