function Circle() {
  let d = dist(mouseX, mouseY, width / 2, height / 2);
  if (d > 180) {
    num = int(random(5));
  }
  for (let i = 0; i < 100; i++) {
    trans = map(d, 0, 180 - i * 0.3, 255, 0);
    fill(r[num], g[num], b[num], trans);
    ellipse(width / 2, height / 2, 90 + i * 1.8);
  }
}

function Triangle() {
 let d = dist(mouseX, mouseY, width / 2, height / 2);
  if (d > 180) {
    num = int(random(5));
  }
  for (let i = 0; i < 100; i++) {
    trans = map(d, 0, 180 - i * 0.3, 255, 0);
    fill(r[num], g[num], b[num], trans);
   triangle(width / 2, height / 2-45-i*0.9, width/2-45-i*0.9, height/2+45+i*0.9, width/2+45+i*0.9, height/2+45+i*0.9);

  }
}

function rectangular() {
  let d = dist(mouseX, mouseY, width / 2, height / 2);
  if (d > 180) {
    num = int(random(5));
  }
  for (let i = 0; i < 100; i++) {
    trans = map(d, 0, 180 - i * 0.3, 255, 0);
    fill(r[num], g[num], b[num], trans);
    rectMode(CENTER);
    rect(width / 2, height / 2, 90 + i * 1.8, 90 + i * 1.8);
  }
}

