function setup() {
    createCanvas(800, 800);

    var image1 = loadImage("https://i.imgur.com/uDitH7i.png");
    var image2 = loadImage("https://i.imgur.com/UGKYunh.png");

    sprite1 = createSprite(400, 400, 100, 100);
    sprite1.addImage("main", image1);
    sprite1.scale = .5;
    sprite1.addSpeed(5, random(100, 500));

    sprite2 = createSprite(50, 50, 100, 100);
    sprite2.addImage("main", image2);
    sprite2.scale = .5;
}


function draw() {
    background("#cccccc");
    drawSprites();
    bounce();
    keyPressed();

    sprite1.overlap(sprite2, explosion);

    function explosion(sprite2, sprite1) {
      sprite2.remove();
      sprite1.remove();
    }

}

function bounce(){
    for (var i = 0; i < allSprites.length; i++) {
    var spri = allSprites[i];
    if (spri.position.x < 0) {
      spri.position.x = .5;
      spri.velocity.x = 5;
    }

    if (spri.position.x > 800) {
      spri.position.x = 799;
    spri.velocity.x = -5;
    }

    if (spri.position.y < 0) {
        spri.position.y = .5;
       spri.velocity.y = 5;
    }

    if (spri.position.y > 800) {
      spri.position.y = 799;
      spri.velocity.y = -abs(spri.velocity.y);
    }
  }
}


function keyPressed() {
  if (keyCode == RIGHT_ARROW) {
    sprite2.setSpeed(2, 0);
  }
  else if (keyCode == DOWN_ARROW) {
    sprite2.setSpeed(2, 90);
  }
  else if (keyCode == LEFT_ARROW) {
    sprite2.setSpeed(2, 180);
  }
  else if (keyCode == UP_ARROW) {
    sprite2.setSpeed(2, 270);
  }
  else if (key == ' ') {
    sprite2.addSpeed(0, 0);
  }
  return false;
}

