module rock(){
    color("grey"){
             intersection_for(i = 
          [ [  0,  0,   0],
 			[ 10, 20, 300],
 			[200, 40,  57],
 			[ 20, 88,  57] ])
{
    rotate(i)
    cube([10, 10, 10], center = true);
}
        }
    }

module leaves(){
    color("green"){
        translate([0,0,cH])cylinder(h=layerH, r1=rad1, r2=rad2, $fn = 4);
translate([0,0,cH+layerH])rotate([0,0,10])cylinder(h=layerH, r1=rad1-rad2, r2=rad2, $fn = 4);
translate([0,0,cH+(2*layerH)])rotate([0,0,20])cylinder(h=layerH, r1=rad1-(2*rad2), r2=0, $fn = 4);
        }
    }

module aspen(){
    union(){
        cylinder(h=cH*2, r=2, $fn = 20);
    translate([0,0,2*cH])cylinder(h =cH*1.5, r1=2, r2 = 0, $fn = 20);
    stems();
        } 
} 

