var myTurtle;

function setup() {
	createCanvas(500, 500);
	noFill();
	stroke(255);
	background(50);
	noLoop();
	myTurtle = new Turtle();
}



function draw() {
	myTurtle.penUp();
	myTurtle.moveTo(250, 450);
	myTurtle.turnTo(-90);
	myTurtle.penDown();
	drawBranch(100);

	// draw a sunflower
    myTurtle.penUp();
	myTurtle.moveTo(375, 350);
	myTurtle.penDown();
    drawSunflower();
    // draw a stem of sunflower
	myTurtle.penUp();
	myTurtle.moveTo(365, 450);
	myTurtle.penDown();
	myTurtle.moveForward(120);

	// draw a sunflower
    myTurtle.penUp();
	myTurtle.moveTo(123, 360);
	myTurtle.penDown();
    drawSunflower();
    // draw a stem of sunflower
	myTurtle.penUp();
	myTurtle.moveTo(115, 440);
	myTurtle.penDown();
	myTurtle.moveForward(100);
}


function drawBranch(length) {

	if (length < 5) {
		return;
	}

	// draw this branch
	myTurtle.moveForward(length);

	// left child
	myTurtle.pushState();
	myTurtle.turnLeft(20);
	drawBranch(length * random(.5, .9));

	myTurtle.popState();

	// right child
	myTurtle.pushState();
	myTurtle.turnRight(30);
	drawBranch(length * random(.5, .9));
	myTurtle.popState();

}

function drawSunflower() {
    for (var i = 0; i < 72; i++) {
        noFill();
    	stroke(255, 255, 255, 180);
    	strokeWeight(1);
		myTurtle.moveForward(70);
		myTurtle.turnRight(200);
	}
}

