const dim = 800;

var delta = 0;
var factor = 0;

function setup() {
  createCanvas(dim, dim);
  colorMode(HSB);
  frameRate(30);
  init();
}

function draw() {
  background('black');
  strokeWeight(1);

  translate(width / 2, height);

  stroke(0);
  fractal(0, 0, 3 * HALF_PI, height / 8, 12);

  delta = map(factor, 0, width, 0, HALF_PI);
  factor += 10;
}

function init() {
  clear();
  background('black');
  factor = 0;
}

function fractal(x, y, theta, length, depth) {
  stroke(abs(theta * 40) % 255, 255, 255);
  strokeWeight(depth);
  line(x, y, x + length * cos(theta), y + length * sin(theta));
  if (depth) {
    fractal(x + length * cos(theta), y + length * sin(theta),
      theta + delta, length * 0.8, depth - 1);
    fractal(x + length * cos(theta), y + length * sin(theta),
      theta - delta, length * 0.8, depth - 1);
  }
}

