	noLoop();
}

function draw()
{

	noFill();
	strokeWeight(1);
	stroke("#a89780");

	mapTexture();

	grid();

	mapText();

	drawRivers();

	drawTreasure();

}

function mapTexture(){

	for (let a = 0; a <= width; a++){

		for (let c = 0; c <= height; c++) {

			l = 0.01;

			push();
			n = noise((a * l), (c * l));
			let r = map(n, 0, 1, 198, 231);
			let g = map(n, 0, 1, 176, 208);
			let b = map(n, 0, 1, 151, 183);
			stroke(r, g, b);
			point(a, c);
			pop();

		}

	}
}

function drawRivers(){

	noiseDetail(5);

	beginShape();

	let off = 0;

	for (let x = 30; x <= width - 30; x++){

		noFill();
		stroke("#2b8c67")

		let y = map(noise(off, 1), 0, 1, 30, height - 30);

		vertex(x, y);

		off += 0.005;

}
	endShape();


	beginShape();
	off = 0;

	for (let x = 30; x <= width - 200; x++){

		noFill();
		stroke("#2b8c67")

		let y = map(noise(off, map(x, 200, width - 30, 1, 2, true)), 0, 1, 30, height - 30);

		// let bias = map(x, 200, width - 30, 0, 100, true);

		let n = (x - 200) / (width - 30 - 200); // gives end value for where x falls in this range
		n = constrain(n, 0, 1);
		n = pow(n, 0.5);
		let bias2 = n * 100;

		vertex(x, y + bias2);

		off += 0.005;

}
	endShape();



	beginShape();
	off = 0;

	for (let y = 300; y <= height-30; y++){

		noFill();
		stroke("#2b8c67")

		let x = map(noise(off, 1), 0, 1, 500, width - 30);

		vertex(x, y);

		off -= 0.005;
}
	endShape();



	beginShape();
	off = 0;

	for (let y = 30; y <= height - 250; y++){

		noFill();
		stroke("#2b8c67")

		let x = map(noise(off, 1), 0, 1, height - 300, 300);

		vertex(x, y);

		off -= 0.005;

}
	endShape();
}

function drawTreasure(){


	noStroke();
	fill("#9a423b75");
	ellipse(random(400, width - 55), random(40, height - 55), 50);


}

function grid(){

	for (let x = margin; x <= width - margin; x += (width - (margin * 2)) / 4) {

		for (let y = margin; y <= height - margin; y += (height - (margin * 2)) / 3) {

			l = 0.5;

			n = noise(x * l);

			line(x + n, margin, x + n, height - margin);
			line(margin, y + n, width - margin, y + n);

		}

	}

}



