class Character{
    constructor(){
      this.x = 5
      this.y = 200
      this.tag = "character"
      this.alpha = 0


    }

    show(r,g,b){
    push()
    fill(r,g,b)
    square(this.x,this.y,30)
    pop()
    fill(0,100,0,this.alpha)
    square(this.x,this.y,30)
    }

    move(x,y){
      this.speedX = x
      this.speedY = y
      //this.x+=this.speedX*10

    //Y pos 
       if(this.speedX>=0.2){
      this.x+=this.speedX*20
      }

      if(this.speedX<=0.2){
      this.x-=this.speedX*30
      }


      if(this.speedY>=0.2){
      this.y-=this.speedY*20
      }

      if(this.speedY<=0.2){
      this.y+=this.speedY*30
      }

    //X pos
      if(this.x<=0){
      this.x=0
      }
      if(this.x>=370){
      this.x=370
      }
      if(this.y<=0){
      this.y=0
      }
      if(this.y>=370){
      this.y=370
      }

    }

    meet(){
        this.alpha+=100
    }

    check(){
       if(175<=this.x && this.x<=225 ){
         console.log(this.x)
         if(175<=this.y && this.y<=225){
           sceneNum = 5
       }
       }
    }


}

class Bonus{

   constructor(x,y){
      this.x = x
      this.y = y
      this.tag = "bonus"


    }

  show(){
      noStroke()
      push()
      fill(0,100,0)
      circle(this.x,this.y,30)
      pop()

  }



}

class Ball{

  constructor(){
    this.speedX=-3
    this.speedY = 4
    this.x = 0;
    this.y=0;
  }

  create(){
    push()
    fill(255,165,0)
    circle(this.x,this.y,30)
    pop()
  }

  move(){

      this.x = this.x+this.speedX;
      this.y = this.y+this.speedY;

       if ( (this.x>width) || (this.x<0) ){
         this.speedX = this.speedX *-1;
       }

       if ( (this.y>height) || (this.y<0) ){
         this.speedY = this.speedY *-1;
       } 

  }

  check(other){

    var ball_dist = dist(this.x,this.y,other.x,other.y)

    if(ball_dist<=50){
      console.log("lose")
      sceneNum = 4
    }
  }

}

