var myTurtle;

function setup() {
	createCanvas(600, 600);
	noFill();
	background("#e6e6e6");
	noLoop();
	myTurtle = new Turtle();
}



function draw() {
	myTurtle.penUp();
	myTurtle.moveTo(250, 600);
	myTurtle.turnTo(-90);
	myTurtle.penDown();
	drawBranch(30);
}


function drawBranch(length) {

	strokeWeight(2);
	stroke("#4e414c");

	if (length < 10) {
		return;
	}

	myTurtle.moveForward(length);
	myTurtle.moveForward(length);
	myTurtle.turnRight(12);

	myTurtle.pushState();

	myTurtle.turnRight(20);
	myTurtle.moveForward(length);
	drawBranch(length * 0.75);

	myTurtle.turnLeft(12);
	myTurtle.moveForward(length);
	drawBranch(length * 0.75);

	myTurtle.turnLeft(12);
	myTurtle.moveForward(length);
	drawBranch(length * 0.70);

	myTurtle.popState();

	myTurtle.turnLeft(20);

	myTurtle.pushState();

	myTurtle.turnLeft(20);
	myTurtle.moveForward(length);
	drawBranch(length * 0.75);

	myTurtle.turnRight(20);
	myTurtle.moveForward(length);
	drawBranch(length * 0.5);

	myTurtle.turnRight(20);
	myTurtle.moveForward(length);
	drawLeaf();

	myTurtle.popState();

}

function drawLeaf() {
	myTurtle.pushState();

	for (i = 0; i < 15; i++) {

		strokeWeight(11);
		stroke("#00bb3b")
		myTurtle.moveForward(1);

		strokeWeight(2);
		stroke("#4e414c");
		myTurtle.turnLeft(30);
	}

	myTurtle.popState();
}

// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.1.9/p5.js

const tileCount = 8;
const delayFactor = 30;

var videoStream;
var tiles = [];


function setup() {
  createCanvas(windowWidth, windowHeight);
  videoStream = createCapture(VIDEO);
  videoStream.hide();
  frameRate(15);
}

function draw() {
  background(255);

  let img = createImage(videoStream.width / 2, videoStream.height / 2);
  img.copy(
    videoStream,
    0, 0, videoStream.width, videoStream.height,
    0, 0, img.width, img.height
  );

  tiles.unshift(img);
  if (tiles.length > delayFactor) {
    tiles.pop();
  }

  let stepX = width / tileCount;
  let stepY = stepX * videoStream.height / videoStream.width;

  if (tiles.length == delayFactor) {
    for (let x = 0; x < width; x += stepX) {
      for (let y = 0; y < height; y += stepY) {
        let index = Math.floor(noise(x * 0.01, y * 0.01) * delayFactor);
        image(tiles[index % delayFactor], x, y, stepX, stepY);
        index++;
      }
    }
  }
}

