var amplitude_slider;
var frequency_slider;

var startX = 50;
var startY = 250;
var endX = 450;
var endY = 50;
//var timeSpeed = 10;

function setup() {
    createCanvas(500, 300);
    angleMode(DEGREES)

    createP('Amplitude');
    amplitude_slider = createSlider(0, 10000, 50);

    createP('Frequency');
    frequency_slider = createSlider(0, 10, 5);

    createP('Time Speed');
    timeSpeed_slider = createSlider(0, 1000, 500);


}


function draw() {
    background(50);
    ellipseMode(CENTER);

    var amplitude = amplitude_slider.value()/100;
    var frequency = frequency_slider.value() 
    var timeSpeed = timeSpeed_slider.value()

    noiseDetail(1, .5);




    fill(255);
    noStroke();



    // study this loop. do you understand how the line of ellipses is created?
    for (i = 0; i < 1; i += .02) {
        var r = frequency
        var x = lerp(startX, endX, i);
        var y = lerp(startY, endY, i);

        // hint: drive offsetX and offsetY with noise() instead of random()
        var offsetX = noise(i*r,timeSpeed,frameCount/100) * amplitude;
        var offsetY = noise(i*r,timeSpeed,frameCount/100) * amplitude;

        ellipse(x  + offsetX, y + offsetY, 10, 10);

    }



}

