  fft = new p5.FFT();
  createCanvas(600, 600);
  noStroke();
}
var counter = 0;
var angle = 2.0;
var offset = 300;
var scalar = 1.5;
var speed = 0.1;
function start() {
  song.play(0, 1, 1, 27, 15);
}
function draw() {
  background(0);
  let c = map(song.currentTime(), 27, 42, 0, 360);

  let r = map(song.currentTime(), 27, 42, 0, 255);
  var vol2 = analyzer.getLevel([0]);
  let g2 = map(vol2, 0, 1, 0, sin(c));
  console.log(g2);
  var vol = analyzer.getLevel();
  let g = map(vol, 0, 1, 0, 255);
  angleMode(DEGREES);
  stroke(r, c, r);
  fill(0, 15, sin(g) * 10);
  // arc(80, 80, 150, 150, 180, 360);
  if (!song.isPlaying()) {
    angle = 2.0;
    speed = 2.0;
    scalar = 3.5;
    start();
    counter += 1;
  }
  if (song.isPlaying()) {
    // console.log(song.currentTime());
    //console.log(r);
  }
  push();
  stroke(255);
  var currentSeconds = song.currentTime();
  text(currentSeconds, 40, 60);
  pop();
  var x = offset - cos(c) * scalar;
  // var x = offset - cos(angle) * scalar;
  var y = offset - sin(angle) * tan(g2) * scalar;
  // stroke(c, 0, c);

  ellipse(x / 10 + 300, y / 10 + 250, 5 + g * 3, 5 + g * 3);
  ellipse(-x / 10 + 300, -y / 10 + 250, 5 + g * 3, 5 + g * 3);
  ellipse(-x / 10 + 300, y / 10 + 250, 5 + g * 3, 5 + g * 3);
  ellipse(x / 10 + 300, -y / 10 + 250, 5 + g * 3, 5 + g * 3);
  for (let i = 0; i < g; i++) {
    if (song.currentTime() > 28 + i) {
      ellipse(x / i / 15 + 300, y / 7 + 250, 5 + g * 2, 5 + g * 2);
    }
    if (song.currentTime() > 28 + i) {
      ellipse(-x / i / 15 + 300, y / 7 + 250, 5 + g * 2, 5 + g * 2);
    }
    if (song.currentTime() > 28 + i) {
      ellipse(-x / i / 15 + 300, -y / 7 + 250, 5 + g * 2, 5 + g * 2);
    }
    if (song.currentTime() > 28 + i) {
      ellipse(x / i / 15 + 300, -y / 7 + 250, 5 + g * 2, 5 + g * 2);
    }
    if (song.currentTime() > 30 + i) {
      ellipse(x / i / 7 + 300, y / 15 + 250, 5 + g * 2, 5 + g * 2);
    }
    if (song.currentTime() > 30 + i) {
      ellipse(-x / i / 7 + 300, -y / 15 + 250, 5 + g * 2, 5 + g * 2);
    }
    if (song.currentTime() > 32 + i) {
      ellipse(
        x / i / 7 + 300,
        -y / 15 + 250 + sin(c) * 10,
        5 + g * 2,
        5 + g * 2
      );
    }
    if (song.currentTime() > 32 + i) {
      ellipse(
        -x / i / 7 + 300,
        y / 15 + 250 + sin(c) * 10,
        5 + g * 2,
        5 + g * 2
      );
    }
    ////
    if (song.currentTime() > 32 + i) {
      ellipse(sin(x) * 10 + 300, y, 5 + g * 2, 5 + g * 2);
    }
  }

  angle += speed;
  scalar += speed;
}

