class Ripple {
    constructor(x, y) {
      this.position = createVector(x, y)
      this.size = random(50, 100)
      // é€æ˜Žåº¦çš„å€¼
      this.lifespan = 255
      this.color = color(255, 255, 255)
      this.sizeStep = random(2, 3)
      // æ˜¾ç¤ºçš„æ—¶é—´
      this.lifeStep = random(2, 10)
    }

    drawShape(x, y, offset, size, color) {
      stroke(color)
      strokeWeight(1)
      noFill()
      ellipse(x + offset, y + offset, size)
    }

    show() {
      // è®¾ç½®é¢œè‰²é€æ˜Žåº¦
      this.color = color(255, 255, 255,this.lifespan)
      // å½“å‰åœ†,é¢œè‰²ç™½è‰²
      this.drawShape(this.position.x, this.position.y, 0, this.size, this.color)
      //åç§»50çš„åœ†,é¢œè‰²å¦ä¸€ç§
      this.drawShape(this.position.x, this.position.y, 50, this.size, color(100))
    }

    update() {
      // åœ†å˜å¤§
      this.size += this.sizeStep
      // æ—¶é—´å˜çŸ­
      this.lifespan -= this.lifeStep
    }
  }
