const pane = new Tweakpane();
const para = {};

const blocks = 30;

var colorPallete = ["f94144", "f3722c", "f8961e", "f9c74f", "90be6d", "43aa8b", "577590"];
var position = 0;

var points = [];
var blockSize;
var bgColor;

const fr = 30;

function setup() {
  createCanvas(windowWidth, windowHeight);
  background(255);
  shuffle(colorPallete, true);
  frameRate(fr);

  initPattern();

  para.dir = 2;
  pane.addInput(para, "dir", {min:-20, max:20});

}

function draw() {
  generatePattern();
  pane.refresh();
}

function getColor() {

  var v = colorPallete[position];
  position++;
  if (position == colorPallete.length) {
    colorPallete = shuffle(colorPallete);
    position = 0;
  }

  return str("#" + v);
}

function mousePressed() {
  initPattern();
}

function initPattern() {
  clear();
  noStroke();
  points = [];

  bgColor = getColor();
  blockSize = int(windowWidth / blocks);
  if (blockSize % 2 == 0)
    blockSize += 1;

  for (let i = 0; i < windowWidth; i ++) {
    points[i] = [];
    for (let j = 0; j < windowHeight; j ++) {
      points[i][j] = int(random(2,2));
    }
  }

}

function generatePattern() {

  let dir = 1;
  let delta, deltaNoise;

  for (let m = 0; m < windowWidth + 1; m += blockSize) {
    for (let n = 0; n < windowHeight + 1; n += blockSize) {
      if ((m + n) % 2 == 0) {
        dir = 5;
        fill(bgColor);
      } else {
        dir = -5;
        fill('black');
      }

      deltaNoise = noise(m / windowWidth, n / windowHeight , frameCount / fr);
      delta = map(deltaNoise, 0.3, 0.7, -blockSize, blockSize);
      circle(m, n, blockSize/10 + para.dir * delta);
    }
  }
}

