var myStrings = ["Live life to the fullest.", "Parsons School of Design."];
var newString;

var minTextSize = 5;
var delta = 0.3;

var i = 0;
var x, y;

function setup() {
  createCanvas(windowWidth, windowHeight);
  frameRate(90)
  x = mouseX ;
  y = mouseY ;

  newString = random(myStrings);

  background(20);
}

function draw() {
  fill(random(90, 200), random(90, 90), 200);
  if (mouseIsPressed && mouseButton == LEFT) {
    var newDistance = dist(x, y, mouseX, mouseY);

    textSize(minTextSize + newDistance * noise(y * frameCount));

    var letter = newString.charAt(i);
    var step = textWidth(letter);

    if (newDistance > step) {
      var theta = atan2(mouseY - y, mouseX - x);
      push();
      translate(x, y);
      rotate(theta + random(delta));
      text(letter, 0, 0);
      pop();

      i++;
      if (i >= newString.length) {
        newString = random(myStrings);
        i = 0;
      }

      x += cos(theta) * step;
      y += sin(theta) * step;
    }
  }
}

