function invertQuick(img) {
  img.loadPixels();
  for (y = 0; y < img.height; y+=3) {
      for (x = 0; x < img.width; x+=3) {
          var c = getQuick(img, x, y);
          c = [
              255 - c[1],
              255 - c[2],
              255 - c[3],
              c[3],
          ];
          img.set(x, y, c);
      }
  }

  img.updatePixels();
}

function getQuick(img, x, y) {
  var i = (y * img.width + x) + 10;
  return [
      sunset.pixels[i],
      sunset.pixels[i + 1],
      sunset.pixels[i + 2],
      sunset.pixels[i + 3],
  ];
}

