var player;
var playerImage, bulletImage, particleImage;
var MARGIN = 40;

function setup() {
  createCanvas(800, 600);
  imageMode(CENTER);

  bulletImage = loadImage('https://i.imgur.com/S29PlbC.png');
  playerImage = loadImage('https://i.imgur.com/OaJfXrg.png');

  player = createSprite(width/2, height/2);
  player.maxSpeed = 6;
  player.friction = 0.02;
  player.setDefaultCollider();
  player.scale = 0.75;

  player.addImage('normal', playerImage);
  player.addAnimation('thrust', 'https://i.imgur.com/OaJfXrg.png', 'https://i.imgur.com/pmau3nj.png', 'https://i.imgur.com/r7vwyp5.png', 'https://i.imgur.com/zDmprGh.png', 'https://i.imgur.com/BL6KrsN.png', 'https://i.imgur.com/aRF5NgF.png');

  asteroids = new Group();
  bullets = new Group();

  for(var i = 0; i<8; i++) {
    var ang = random(360);
    var px = width/2 + 1000 * cos(radians(ang));
    var py = height/2 + 1000 * sin(radians(ang));
    createAsteroid(px, py);
  }
}

function draw() {
  background("#0044bb");

  fill(255);
  textAlign(CENTER);
  text('Change Angle: Left and Right Arrows â€” Move Forward: Up Arrow â€” Shoot: Spacebar', width/2, 20);

  for(var i=0; i<allSprites.length; i++) {
    var s = allSprites[i];
    if(s.position.x<-MARGIN) s.position.x = width+MARGIN;
    if(s.position.x>width+MARGIN) s.position.x = -MARGIN;
    if(s.position.y<-MARGIN) s.position.y = height+MARGIN;
    if(s.position.y>height+MARGIN) s.position.y = -MARGIN;
  }

  asteroids.overlap(bullets, asteroidHit);

  player.bounce(asteroids);

  if(keyDown(LEFT_ARROW))
    player.rotation -= 4;
  if(keyDown(RIGHT_ARROW))
    player.rotation += 4;
  if(keyDown(UP_ARROW))
  {
    player.addSpeed(0.2, player.rotation);
    player.changeAnimation('thrust');
  }
  else
    player.changeAnimation('normal');

  if(keyWentDown(' '))
  {
    var bullet = createSprite(player.position.x, player.position.y);
    bullet.addImage(bulletImage);
    bullet.setSpeed(10+player.getSpeed(), player.rotation);
    bullet.life = 30;
    bullets.add(bullet);
  }

  drawSprites();

}

function createAsteroid(x, y) {

  var img;

  var a = createSprite(x, y);

  img = loadImage('assets/cf_sprite_0'+ floor(random(0, 13)) +'.png');

  a.addImage(img);
  a.setSpeed(2.5, random(360));
  a.rotationSpeed = 0.5;

  a.scale = 0.2;

  a.setDefaultCollider();
  asteroids.add(a);
  return a;
