    }

    translate([-25, -25, -0.1]) color("lightgreen") cube([50, 50, 0.1]);

    translate([0.5 * walls_to_draw * wall_length, 0.5 * walls_to_draw * wall_length, 2 * wall_height]) {
        union() {
            color("lightgrey") 
                cylinder(h = 2.5 * wall_height, r = 0.05, $fn = 30);
            color("Red") translate([0, 0, 2.2 * wall_height]) cube([0.5, 0.05, 0.2]);
        }
