var bloom_slider;

function setup() { 
  createCanvas(400, 400);

  createP('Bloom');
  bloom_slider = createSlider(0, 200, 0);
}

function draw() { 
  var bloom = bloom_slider.value();
  background(220);


  line(width / 2, height / 2, 0, 0);
  line(width / 2, height / 2, 0 + bloom, 0);
  line(width / 2, height / 2, 0 + bloom*2, 0);
  line(width / 2, height / 2, 0 + bloom*2, bloom);
  line(width / 2, height / 2, 0 + bloom*2, bloom*2);
  line(width / 2, height / 2, 0 + bloom, bloom*2);
  line(width / 2, height / 2, 0, bloom*2);
  line(width / 2, height / 2, 0, bloom);



  stroke(50);
  strokeWeight(3);

  circle_1(bloom);
  circle_2(bloom);

}


function circle_1(bloom) {
  stroke(50);
  strokeWeight(3);

  const steps = 8;
  const centerX = width / 2;
  const centerY = height / 2;
  const radius = bloom;
  for (let step = 1; step <= steps; step++) {
    const a1 = map(step - 1, 0, steps, 0, 2 * PI);
    const a2 = map(step, 0, steps, 0, 2 * PI);
    const x1 = centerX + sin(a1) * radius;
    const x2 = centerX + sin(a2) * radius;
    const y1 = centerY + cos(a1) * radius;
    const y2 = centerY + cos(a2) * radius;
    line(x1, y1, x2, y2);
  }
}

function circle_2(bloom) {
  stroke(50);
  strokeWeight(3);

  const steps = 8;
  const centerX = width / 2;
  const centerY = height / 2;
  const radius = bloom / 2;
  for (let step = 1; step <= steps; step++) {
    const a1 = map(step - 1, 0, steps, 0, 2 * PI);
    const a2 = map(step, 0, steps, 0, 2 * PI);
    const x1 = centerX + sin(a1) * radius;
    const x2 = centerX + sin(a2) * radius;
    const y1 = centerY + cos(a1) * radius;
    const y2 = centerY + cos(a2) * radius;
    line(x1, y1, x2, y2);
  }
}

