    // const static = 1;
    // const static = (x + y) % 2;

    const ripple = sin(d + frameCount * 0.1) * 0.5 + 0.5;
    g.pixels[i + 0] = map(d, -0, 10, ripple, 0, true) * 255;
    g.pixels[i + 1] = map(d, -10, 10, ripple, 0, true) * 255;
    g.pixels[i + 2] = map(d, -5, 10, ripple + static, 0, true) * 255;
    g.pixels[i + 3] = 255;
