  for (let powerUP of powerUPs) {
    powerUP.step();
  }
  for (let powerUP of powerUPs) {
    powerUP.collisions();
  }
  for (let boss of bosses) {
    boss.step();
  }
  for (let boss of bosses) {
    boss.collisions();
  }
  ///
  if (enemies.length === 0) {
    if (!waitingForLevelSpawn) {
      console.log("your score is " + score + "!!!");
      level += 1;
      console.log(level);
      setTimeout(spawnLevel, 2000);
      waitingForLevelSpawn = true;
    }
  }

  if (ship.health < 0) {
    console.log("your score is" + " " + score + "!!!");
    score = 0;
    if (!waitingForLevelSpawn) {
      enemies.splice(0, enemies.length);
      bosses.splice(0, bosses.length);
      console.log(level);
      setTimeout(spawnLevel, 2000);
      ship.health = 100;
      // score = 0
      level = 0;
      ship.powerscore = 0;
      waitingForLevelSpawn = true;
    }
  }
}

