function spawnLevel() {
  let moreMobs = hashit(level);
  console.log("Current Level:" + " " + level);
  console.log(moreMobs);
  if (level != 5) {
    for (let row = 0; row < 10 + moreMobs / 2; row++) {
      for (let col = 0; col < 10 + moreMobs; col++) {
        enemies.push(new Enemy(col * 30, row * 30 + 50, level * 10 + 10));
      }
    }
    waitingForLevelSpawn = false;
  }
  /// BOSS LEVEL
  if (level == 5) {
    for (let row = 0; row < 5; row++) {
      for (let col = 0; col < 5; col++) {
        enemies.push(new Enemy(col * 30, row * 30 + 50, 100));
      }
    }
    for (let row = 10; row < 15; row++) {
      for (let col = 10; col < 15; col++) {
        bosses.push(new BossMob(col *30, row *30, 100));
      }
    }
    waitingForLevelSpawn = false;
  }

  //////////

}

function hashit(k) {
  let a = (k * (k + 3)) % 16;
  return a;
}

function draw() {
  ctx.fillStyle = "black";
  ctx.fillRect(0, 0, canvas.width, canvas.height);

  ship.draw();

  for (let bullet of bullets) {
    bullet.draw();
  }
  for (let enemy of enemies) {
    enemy.draw();
  }
  for (let powerUP of powerUPs) {
    powerUP.draw();
  }
  for (let boss of bosses) {
    boss.draw(); 
  }
}
function onFrame() {
  step();
  draw();

  window.requestAnimationFrame(onFrame);
}

class Ship {
  constructor() {
    this.x = canvas.width * 0.5;
    this.y = canvas.height - 70;
    this.w = 50;
    this.h = 80;
    this.cooldown = 0;
    this.health = 100;
    this.powerscore = 0;
    this.tookDamage = 0;
    this.tookPowerUP = 0;
  }

  step() {
    if (controls.left) this.x -= 10;
    if (controls.right) this.x += 10;
    if (controls.fire) this.fire();
    if (controls.bomb) this.bomb();
    if (this.cooldown > 0) this.cooldown--;
    if (this.tookDamage > 0) this.tookDamage--;
    if (this.tookPowerUP > 0) this.tookPowerUP--;
  }

  remove() {
    this.tookDamage += 10; // frames for getting hit
    this.health -= 10; // hp lost

    console.log("your health is" + " " + this.health);
    console.log("ouch");
  }
  fire() {
    if (this.powerscore < 5 && this.cooldown === 0) {
      bullets.push(new Bullet(this.x, this.y, -20));
      this.cooldown = 1;
    }
    if (this.powerscore >= 5 && this.powerscore < 7) {
      if (this.cooldown === 0) {
        bullets.push(new Bullet(this.x - 20, this.y, -20));
        bullets.push(new Bullet(this.x + 20, this.y, -20));
        this.cooldown = 2;
      }
    }
    if (this.powerscore >= 7) {
      if (this.cooldown === 0) {
        bullets.push(new Bullet(this.x - 20, this.y, -20));
        bullets.push(new Bullet(this.x + 20, this.y, -20));
        bullets.push(new Bullet(this.x, this.y, -20));
        this.cooldown = 3;
      }
    }
    if (this.powerscore >= 12) {
      if (this.cooldown === 0) {
        bullets.push(new Bullet(this.x - 20, this.y, -10));
        bullets.push(new Bullet(this.x + 20, this.y, -30));
        bullets.push(new Bullet(this.x, this.y, -10));
        this.cooldown = 3;
      }
    }
  }
 bomb() {
  if (this.powerscore >= 12) {
    if (this.cooldown === 0) {
   for (let i = this.x - 40; i< this.x + 40; i++ ){
  bullets.push(new Bullet(i, this.y, -7));
  this.cooldown = 30;
   }
  }
}
 }

  draw() {
    //console.log(this.tookDamage);
    //console.log(this.tookPowerUP);

    ctx.fillStyle = "#7453c2";

    if (this.tookDamage > 0) {
      ctx.fillStyle = "red";
    }
    if (this.tookPowerUP > 0 && this.tookDamage < 1) {
      ctx.fillStyle = "blue";
    }
    ctx.fillRect(this.x - this.w * 0.5, this.y - this.h * 0.5, this.w, this.h);

    ctx.fillRect(
      this.x - this.w * 0.6,
      this.y - this.h * 0.6,
      this.w * 0.5,
      this.h * 0.5
    );

    ctx.fillRect(
      this.x + this.w * 0.1,
      this.y - this.h * 0.6,
      this.w * 0.5,
      this.h * 0.5
    );
  }
  enhance() {
    console.log("my power is " + this.powerscore);
    this.powerscore += 1;
    this.health += 1;
    this.tookPowerUP += 10;
  }
}

class Bullet {
  constructor(x, y, dY = -10, targets = enemies) {
    this.x = x;
    this.y = y;
    this.dY = dY;
    this.targets = targets;
    this.w = 5;
    this.h = 10;
    this.targetstwo = bosses;
  }

  step() {

    this.y += this.dY;

  }

  collisions() {
    for (let target of this.targets) {
      if (pointRectCollision(this, target)) {
        target.remove();
        this.remove();
      }
    }
    for (let target of this.targetstwo) {
      if (pointRectCollision(this, target)) {
        target.remove();
        this.remove();
      }
    }
    if (this.y < -20) this.remove();
  }

  remove() {
    const index = bullets.indexOf(this);
    if (index > -1) {
      bullets.splice(index, 1);
    }
  }
  draw() {
    ctx.fillStyle = "yellow";
    ctx.fillRect(this.x - this.w * 0.5, this.y - this.h * 0.5, this.w, this.h);
  }
}

class PowerUP {
  constructor(x, y, dY = 2, targets) {
    this.x = x;
    this.y = y;
    this.dY = dY;
    this.targets = targets;
    this.w = 15;
    this.h = 15;
  }
  step() {
    this.y += this.dY;
  }

  collisions() {
    for (let target of this.targets) {
      if (pointRectCollision(this, target)) {
        target.enhance();

        this.remove();
      }
    }

    if (this.y > canvas.height) this.remove();
  }

  remove() {
    const index = powerUPs.indexOf(this);
    if (index > -1) {
      powerUPs.splice(index, 1);
    }
  }
  draw() {
    ctx.fillStyle = "Red";
    ctx.fillRect(this.x - this.w * 0.5, this.y - this.h * 0.5, this.w, this.h);
  }

  // laser()
  // sphere()
}

function getRandomIntInclusive(min, max) {
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min + 1) + min); //The maximum is inclusive and the minimum is inclusive
}

class Enemy {
  constructor(x, y, health) {
    this.x = x;
    this.y = y;
    this.w = 25;
    this.h = 25;
    //this.dX = 3 + level / 2;
    this.dX = 3 + level / 2;
    if (level == 5) {
      this.dX = 10;
    }
    this.health = health;
  }

  step() {
    // this.x += this.dX;
    //     if (level ==0 ){
    //  this.dX = 1;
    //       this.x += this.dX
    //    // console.log(this.dX)
    //     }
    this.x += this.dX;
    if (Math.random() < 0.0002 + level * 0.0002) {
      this.fire();
    }
    if (Math.random() < 0.00005) {
      this.spawnPowerUp();
    }
  }

  fire() {
    bullets.push(
      new Bullet(
        this.x,
        this.y,
        getRandomIntInclusive(level + 3, level + 5) + level / 2,
        [ship]
      )
    );
  }

  spawnPowerUp() {
    //console.log("spawning powerup");
    powerUPs.push(new PowerUP(this.x, this.y, 5, [ship]));
  }

  collisions() {
    if (this.x > canvas.width) {
      for (let enemy of enemies) {
        enemy.dX = -Math.abs(this.dX);
      }
    }
    //   if(level == 0){
    //     this.dX == -10
    //   if (this.x > canvas.width) {
    //     for (let enemy of enemies) {
    //       enemy.dX = -Math.abs(this.dX);
    //     }
    //   }
    // }
    if (this.x < 0) {
      for (let enemy of enemies) {
        enemy.dX = Math.abs(this.dX);
      }
    }
  }

  remove() {
    this.health -= 10;
    //console.log(this.health);
    if (this.health < 0) {
      const index = enemies.indexOf(this);
      if (index > -1) {
        enemies.splice(index, 1);
        score += 10 + 10 * level;
      }
    }
  }

  draw() {
    if (level < 3) {
      ctx.fillStyle = `rgb(
        ${Math.floor(25)},
        ${Math.floor(100.5 + 42.5 * level)},
        0)`;
      ctx.fillRect(
        this.x - this.w * 0.5,
        this.y - this.h * 0.5,
        this.w,
        this.h
      );
    } else
      ctx.fillStyle = `rgb(
    ${Math.floor(25)},
    ${Math.floor(0)},
    ${Math.floor(42.5 + 42.5 * level)})`;
    ctx.fillRect(this.x - this.w * 0.5, this.y - this.h * 0.5, this.w, this.h);
  }
}

function pointRectCollision(p, r) {
  return (
    p.x > r.x - r.w * 0.5 &&
    p.x < r.x + r.w * 0.5 &&
    p.y > r.y - r.h * 0.5 &&
    p.y < r.y + r.h * 0.5
  );
}

