    for(let i=0; i<5; i++){
        push();
        translate(random(width), random(height));
        let alpha = random(1, 100);
        let beta = random(20, 200);

        noStroke();
        fill(random(360), 200, 100);
        beginShape();
        let theta = random(30, 120);

        for(let j = 0; j<360; j++){
         let gamma = sin(theta * sin(j * beta));

         let x = theta * gamma * cos(j * sin(j * beta));
         let y = theta * gamma * sin(j * sin(j * beta));

         vertex(x, y);
        }
        endShape(CLOSE);
        pop();
    }
}

