function nikki(counter){

  background(245);

  let count = counter;

  while(count){
    nikkiCanvas();
    count -= 1;
  }

  fill('black');
  textSize(16);
  text("Iteration: "+str(counter), 10, str(dim-20));

  // saveCanvas(str(counter), 'jpg');
}

function nikkiCanvas(){

  let lines = [{}, {}, {}];

  lines[0].x0 = random(10, dim-threeInch);
  lines[0].y0 = random(10, dim-threeInch);
  lines[0].x1 = lines[0].x0 + threeInch;
  lines[0].y1 = lines[0].y0;

  let spacing = random(30,100);

  lines[1].x0 = lines[0].x0;
  lines[1].y0 = lines[0].y0 + spacing;
  lines[1].x1 = lines[0].x0 + threeInch;
  lines[1].y1 = lines[1].y0;

  lines[2].x0 = lines[1].x0;
  lines[2].y0 = lines[1].y0 + spacing;
  lines[2].x1 = lines[1].x0 + threeInch;
  lines[2].y1 = lines[2].y0;

  for(let i=0; i<3; i++){
    line(lines[i].x0, lines[i].y0, lines[i].x1, lines[i].y1);
  }

  let selectedLine = random(lines);
  let randomPoint = {
    x: random(selectedLine.x0+40, selectedLine.x1-40),
    y: selectedLine.y0
  };

  line(randomPoint.x, randomPoint.y, randomPoint.x, randomPoint.y+random(200));
  line(randomPoint.x-40, randomPoint.y, randomPoint.x-40, randomPoint.y+random(-400, 400));
  line(randomPoint.x+40, randomPoint.y, randomPoint.x+40, randomPoint.y+random(-400, 400));

}

