function setup() {
  createCanvas(600, 600);

  bar = createSprite(width / 2, height - 50, 80, 20);
  bar.shapeColor = color(255);

  makeBricks();
  leftWall = createSprite(0, 300, 30, 600);
  leftWall.immovable = true;

  rightWall = createSprite(600, 300, 30, 600);
  rightWall.immovable = true;

  topWall = createSprite(300, 0, 600, 30);
  topWall.immovable = true;

  bottomWall = createSprite(300, 600, 600, 30);
  bottomWall.immovable = true;

  walls = new Group();

  walls.add(leftWall);
  walls.add(rightWall);
  walls.add(topWall);
  walls.add(bottomWall);

  mainSprite = createSprite(300, 300, 30, 30);

  mainSprite.setSpeed(5, random(0, 360));
}

function draw() {
  background(51);

  mainSprite.collide(bricks, getScore);
  mainSprite.collide(bar, changeDirection);
  mainSprite.bounce(walls);
  bar.displace(mainSprite);
  bar.bounce(walls);
  drawSprites();

  textSize(32);

  if (bricks.length > 0) {
    text(score, 30, height - 30);
    console.log(bricks.length);
  }
  if (bricks.length == 0) {
    makeBricks();
    mainSprite.position.x = 200;
    mainSprite.position.y = 200;
  }
}
