let sliderC;
function setup() {
  createCanvas(510, 510);
  createP("Hot Sauce");
  sliderC = createSlider(1, height, height / 2, 0.01);
  sliderC.style("width", "80px");
  noStroke();
}

function draw() {
  background(0);
  makeMesh();
}
function makeMesh() {
  for (let x = 10; x < width; x += 5) {
    for (let y = 10; y < height; y += 5) {
      valC = sliderC.value();
      let vC = map(sin(x * 0.001) * width, 0.1, valC, 1, 10);
      let vY = map(sin(x * 0.02) * 10, 0.1, valC, 1, 10);
      let vM = map(sin(x * 0.02) * 10, valC, 1, 1, 10);
      push();
      rotate((x / valC) * PI);
      noFill();
      stroke(255, 0, 255);
      strokeWeight(2);
      beginShape(POINTS);
      vertex(x * vC + 255, y * vC);
      vertex(x * vC, y);
      vertex(x * vC, y);
      vertex(x * vC, y);
      endShape();
      pop();
      push();
      rotate(x / valC);
      noFill();
      stroke(0, 255, 255);
      strokeWeight(2);
      line(x, y, x, y);
      beginShape(POINTS);
      vertex(x * vM + 255, y);
      vertex(x * vM + 255, y);
      vertex(x * vM, y);
      vertex(x * vM, y);
      endShape();
      pop();
      push();
      rotate(x / valC);
      noFill();
      stroke(255, 255, 0);
      strokeWeight(2);
      beginShape(POINTS);
      vertex(x * vY + 255, y);
      vertex(x * vY + 255, y);
      vertex(x * vY, y);
      vertex(x * vY, y);
      endShape();
      pop();
    }
  }
}

