// Truchet Tiles Noise
// Mouse Click to change Pattern
// Press Spacebar to Start/Stop Canvas Recording

// Recording Reference Sketch
// https://editor.p5js.org/lisajamhoury/sketches/JMSVEKmx8

// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.1.9/p5.js
// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.8.0/addons/p5.dom.min.js

const dim = 800;
const blocks = 31;

var colorPallete = ["f94144", "f3722c", "f8961e", "f9c74f", "90be6d", "43aa8b", "577590"];
var position = 0;

var canvasPoints = [];
var blockSize;
var bgColor;

var recording = false;
var recorder;
var chunks = [];

const fr = 30;

function setup() {
  createCanvas(dim, dim);
  background(200);
  shuffle(colorPallete, true);
  frameRate(fr);

  // initalize recorder
  record();

  initPattern();
}

function draw() {
  generatePattern();
}

// code from class site
function getColor() {
  // find the value at the current position in the deck
  var v = colorPallete[position];

  // change the position for next time
  position++;

  // if we run out of "cards", shuffle and start over from the top
  if (position == colorPallete.length) {
    colorPallete = shuffle(colorPallete);
    position = 0;
  }

  // return the value
  return str("#" + v);
}

function mousePressed() {
  initPattern();
}

function initPattern() {
  clear();
  noStroke();
  canvasPoints = [];

  bgColor = getColor();
  blockSize = int(dim / blocks);
  if (blockSize % 2 == 0)
    blockSize += 1;

  for (let i = 0; i < dim; i += blockSize) {
    canvasPoints[i] = [];
    for (let j = 0; j < dim; j += blockSize) {
      canvasPoints[i][j] = int(random(2));
    }
  }

}

function generatePattern() {

  for (let i = 0; i < dim; i += blockSize) {
    for (let j = 0; j < dim; j += blockSize) {
      if (canvasPoints[i][j]) {
        fill('black');
      } else {
        fill(bgColor);
      }
      rect(i, j, blockSize, blockSize);
    }
  }

  let dir = 1;
  let delta, deltaNoise;

  for (let m = 0; m < dim + 1; m += blockSize) {
    for (let n = 0; n < dim + 1; n += blockSize) {
      if ((m + n) % 2 == 0) {
        dir = 1;
        fill(bgColor);
      } else {
        dir = -1;
        fill('black');
      }

      deltaNoise = noise(m / (dim / 2), n / (dim / 2), frameCount / fr);
      delta = map(deltaNoise, 0.3, 0.7, -blockSize / 3, blockSize / 3);
      circle(m, n, blockSize + dir * delta);
    }
  }
}

function record() {
  chunks.length = 0;

  let stream = document.querySelector('canvas').captureStream(fr);

  recorder = new MediaRecorder(stream);

  recorder.ondataavailable = e => {
    if (e.data.size) {
      chunks.push(e.data);
    }
  };

  recorder.onstop = exportVideo;

}

function exportVideo(e) {
  var blob = new Blob(chunks, {
    'type': 'video/mp4'
  });

  // Draw video to screen
  var videoElement = document.createElement('video');
  videoElement.setAttribute("id", Date.now());
  videoElement.controls = true;
  document.body.appendChild(videoElement);
  videoElement.src = window.URL.createObjectURL(blob);

  // Download the video 
  var url = URL.createObjectURL(blob);
  var a = document.createElement('a');
  document.body.appendChild(a);
  a.style = 'display: none';
  a.href = url;
  a.download = 'truchetTiles.mp4';
  a.click();
  window.URL.revokeObjectURL(url);

}

function keyPressed() {
  recording = !recording

  if (keyCode === 32 && recording) { // SPACEBAR

    console.log("recording started!");
    recorder.start();
  }

  // if we are recording, stop recording 
  if (keyCode === 32 && !recording) { // SPACEBAR
    console.log("recording stopped!");
    recorder.stop();
  }
}

