function draw() {
  let bloom = bloomslider.value();
  let val = slider.value();
  background(val);

  //blendMode(DIFFERENCE);

  line(0, 0, 0, 0);
  line(0, 0, 0 + bloom, 0);
  line(0, 0, 0 + bloom * 2, 0);
  line(0, 0, 0 + bloom * 2, bloom);
  line(0, 0, 0 + bloom * 2, bloom * 2);
  line(0, 0, 0 + bloom, bloom * 2);
  line(0, 0, 0, bloom * 2);
  line(0, 0, 0, bloom);




  stroke(255);
  strokeWeight(4);

  circle_1(bloom);


}


function circle_1(bloom) {
  stroke(255);
  strokeWeight(2);



  const steps = 8;
  const centerX = 0;
  const centerY = 0;
  const radius = bloom;
  for (let step = 1; step <= steps; step++) {
    const a1 = map(step - 1, 0, steps, 0, 2 * PI);
    const a2 = map(step, 0, steps, 0, 2 * PI);
    const x1 = centerX + sin(a1) * radius;
    const x2 = centerX + sin(a2) * radius;
    const y1 = centerY + cos(a1) * radius;
    const y2 = centerY + cos(a2) * radius;
    line(x1, y1, x2, y2);
  }
}

